<script>

export default {
    name: 'CreateLesson',
    data() {
        return {
            courseSectionId: this.$route.query.courseSectionId,
            bankCode: 'NCB'

        }
    },
    components: {

    },
    computed: {
        cartItems() {
            return this.$store.state.cart;
        },
        total() {
            return this.$store.state.totalCartMoney;
        }
    },
    mounted() {


    },
    methods: {
        removeItem(index) {
            this.$store.dispatch('deleteItemFormCart', index);
        },
        checkOut() {
            if (this.bankCode && this.total) {
                let payload = {
                    bankCode: this.bankCode,
                    total: this.total
                }
                let transactionObj = new Object();
                let newLst = this.cartItems.map(item => {
                    return {
                        id: item.id,
                        type: item.type
                    };
                });
                transactionObj.type = 'checkout'
                transactionObj.items = newLst;
                transactionObj.total = this.total;
          
                localStorage.setItem('transactionObj',  JSON.stringify(transactionObj));
                this.$store.dispatch('goPaidPage', payload);
            } else {
                this.$swal.fire({
                    icon: 'error',
                    title: 'Oh no',
                    text: 'Some thing wrong!',
                    footer: '<a href="">Go to cart?</a>'
                });
            }

        }
    },
}
</script>